package Assignment;

public class Swap_Two_Varables {

	public static void main(String[] args) {
		
		int x=100,y=200;
		
		System.out.println("Before Swap");
		System.out.println("x="+x);
		System.out.println("y="+y);
		// TODO Auto-generated method stub
         int temp=x;
         x=y;
         y=temp;
         
        System.out.println("After Swap");
 		System.out.println("x="+x);
 		System.out.println("y="+y);
         
	}

}
