package CoditionalStatements;

public class EvenOdd {

	public static void main(String[] args) {
		
		int a=11;
		
		if(a%2==0)
		{
		// TODO Auto-generated method stub
         System.out.println("The number is Even");
	}
		else
		{
			System.out.println("The number is Odd");
		}
}
}